INSERT INTO `cocy_races_traits` (`race`, `sequence`, `intitule`, `description`) VALUES
('elfe', 2, 'Description', 'Les elfes sont grands et gracieux, mais fins et fragiles.'),
('nain', 2, 'Description', 'Les nains sont petits et trapus, très résistants, mais un peu lents.'),
('orc', 2, 'Description', 'Les orcs sont puissants et solidement bâtis, bien qu’un peu moins vifs d’esprit et séduisants selon les critères des races dominantes.'),
('troll', 2, 'Description', 'Les trolls sont des géants pouvant atteindre près de deux mètres cinquante. Leur peau comporte des excroissances épaisses et parfois même des protubérances pouvant ressembler à des cornes.');
