INSERT INTO `coc_voies` (`voie`, `nom`, `notes`, `type`, `pfx_deladu`, `equipement`) VALUES
('1ere-demon', 'Démon', 'Voie première des démons', '', '0', ''),
('1ere-endogene', 'Endogène', 'Voie première des mutants endogènes', '', '2', ''),
('1ere-exogene', 'Exogène', 'Voie première des mutants exogènes', '', '2', ''),
('1ere-loup-garou', 'Loup-garou', 'Voie première des loups-garous', '', '0', ''),
('1ere-psychogene', 'Psychogène', 'Voie première des mutants psychogènes', '', '0', ''),
('1ere-sainte', 'Sainte', 'Voie première des Anges', '', '3', ''),
('1ere-vampire', 'Vampire', 'Voie première des vampires', '', '0', ''),
('1ere-voile', 'Voile', 'Voie première des êtres féériques', '', '0', ''),
('adaptation-au-monde', 'Adaptation au monde', 'Pour aller parmi les hommes', '', '2', ''),
('arcane-dargent', 'Arcane d\'argent', '', '', '2', ''),
('arcane-dazur', 'Arcane d\'azur', '', '', '2', ''),
('arcane-de-gueules', 'Arcane de gueules', '', '', '2', ''),
('arcane-de-sable', 'Arcane de sable', '', '', '2', ''),
('arcane-de-sinople', 'Arcane de sinople', '', '', '2', ''),
('arcane-dor', 'Arcane d\'or', '', '', '2', ''),
('archeologie', 'Archéologie', 'Équipement : livre sur une civilisation perdue, tenue de randonnée, matériel de fouille.', '', '2', ''),
('argent', 'Argent', 'Équipement : vêtements et accessoires de luxe (montre, bijoux, etc.). 1 000 $ ou € en liquide.', '', '2', ''),
('armes-a-feu', 'Armes à feu', 'Équipement : un pistolet et un fusil au choix (en accord avec le MJ), des munitions.', '', '3', ''),
('arts', 'Arts', 'Équipement : de quoi pratiquer son art, tenue à la mode.', '', '3', ''),
('attrait-de-la-nuit', 'Attrait de la nuit (CHA)', '', '', '2', ''),
('bloqueur', 'Bloqueur (DEX)', 'Un bloqueur réduit à l’impuissance ses adversaires. Le joueur doit choisir un médium : le moyen que le mutant utilise pour rendre efficace son pouvoir, comme de la glace, des toiles d’araignée, des plantes ou encore un champ de force (remplacer DEX par PER).', '', '0', ''),
('bouclier-de-la-foi', 'Bouclier de la foi', '', '', '0', ''),
('brouilleur', 'Brouilleur (INT)', 'Un brouilleur utilise son don psychique pour disparaître de la conscience des créatures présentes.', '', '0', ''),
('champion', 'Champion', 'Cette voie est destinée à un héros redoutable ou un champion d’arts martiaux. Elle peut aussi être appliquée à une créature particulièrement redoutable. Les PNJ dotés de cette voie obtiennent une caractéristique supérieure (indiqué par un *) au choix parmi FOR, DEX ou CON.', 'creat', '0', ''),
('charite', 'Charité', '', '', '1', ''),
('chef-darmee', 'Chef d\'armée', 'Cette voie permet de créer des groupes armés plus coriaces. Assignez au chef du groupe de 1 à 3 rangs, chaque rang augmente son NC de +1. \r\nLes PNJ dotés de cette voie obtiennent le CHA en caractéristique supérieure (indiqué par un *).', 'creat', '0', ''),
('coeur-de-la-nuit', 'Coeur de la nuit (INT)', 'Quartier de lune', '', '0', ''),
('cogneur', 'Cogneur (FOR)', 'Un cogneur renforce son corps et libère ses instincts primaires pour taper fort.', '', '0', ''),
('cogneur-creature', 'Cogneur', 'Les créatures dotées de cette capacité obtiennent la CON en caractéristique supérieure (indiqué par un *).', 'creat', '0', ''),
('colere-divine', 'Colère divine', '', '', '1', ''),
('colosse-creature', 'Colosse', 'Cette voie est à réserver aux créatures de grande taille, les capacités présentées n’affectent que les cibles de taille inférieure à la créature.\r\nLes créatures dotées de cette voie obtiennent la FOR en caractéristique supérieure (indiqué par un *).', 'creat', '0', ''),
('copieur', 'Copieur (CHA)', 'Un copieur est un mutant qui peut reproduire les pouvoirs des autres mutants.', '', '0', ''),
('corporations', 'Corporations', 'Le joueur doit choisir une corporation : armée, médecine, université, crime organisé, show-biz, police, etc. Équipement : costume, un objet symbolique.', '', '3', ''),
('corps-a-corps', 'Corps à corps', 'Équipement : un poignard, éventuellement une autre arme en accord avec le MJ.', '', '0', ''),
('corruption', 'Corruption', '', '', '1', ''),
('coureur', 'Coureur (CON)', 'Le coureur est capable de se déplacer à une vitesse extraordinaire.', '', '0', ''),
('creatures-volantes', 'Créatures volantes', 'Les créatures dotées de cette capacité obtiennent la SAG en caractéristique supérieure (indiqué par un *).', 'creat', '3', ''),
('danger', 'Danger', 'Équipement : une trousse de secours, une corde d’escalade.', '', '0', ''),
('demon', 'Démon', '', '', '0', ''),
('densifieur', 'Densifieur (FOR)', 'Un densifieur contrôle la gravité.', '', '0', ''),
('discours', 'Discours', 'Équipement : un lot de carte de visite, une trousse de maquillage.', '', '0', ''),
('divination', 'Divination', 'La magie de la divination pourrait éventuellement utiliser la PER au lieu de l’INT comme caractéristique de magie (remplacez alors tous les Mod. d’INT par Mod. de PER).', '', '1', ''),
('dominateur', 'Dominateur (CHA)', 'Le dominateur utilise sa force mentale pour contrôler ses victimes. Si une victime résiste à un pouvoir de contrôle, elle y est immunisée pour 24 heures.', '', '0', ''),
('dragon', 'Dragon (CHA)', 'Un être qui descend d’une créature puissante et majestueuse parfois aussi appelé linnorm ou wyverne.', NULL, '0', ''),
('dryade', 'Dryade (INT)', 'Un être proche de la forêt et de la magie, aussi appelé elfe ou sidhe.', '', '1', ''),
('dwergar', 'Dwergar (PER)', 'Un être courtaud mais solide, qui aime les souterrains et parle aux pierres, aussi appelé gnome ou nain.', '', '0', ''),
('elixirs', 'Elixirs', 'Chaque jour, le personnage peut créer jusqu’à deux élixirs par rang acquis dans la voie. N’importe quel individu peut consommer un élixir et en bénéficier, même s’il n’est pas lui-même magicien. Produire un lot d’élixirs prend une heure, quel qu’en soit le nombre. Ce sont des mélanges instables qui deviennent inopérants après 24 heures. Le joueur doit donc en noter l’heure de fabrication. Passé 24 heures, le personnage peut régénérer les élixirs en seulement 10 minutes, mais ils comptent alors parmi les potions créées pour la journée (et les PM ne sont pas récupérés). L’alchimiste doit dépenser 1 PM par élixir produit. Ces PM sont déduits du nombre de PM max du personnage et ne sont pas récupérés avant 24 heures.', '', '3', ''),
('envouteur', 'Envoûteur', 'La magie de la domination pourrait éventuellement utiliser le CHA au lieu de l’INT comme caractéristique de magie (remplacez alors tous les Mod. d’INT par Mod. de CHA sauf exception signalée par °). Note : Si une cible résiste à un sort de cette voie, elle y est immunisée pendant 24 h.', '', '2', ''),
('esprit-farouche', 'Esprit farouche (PER)', '', '', '2', ''),
('esprits-de-la-nuit', 'Esprits de la nuit (PER)', 'Croissant de lune', '', '3', ''),
('esprits-totemiques', 'Esprits totémiques', 'Communication avec les esprits des animaux', '', '3', ''),
('estompeur', 'Estompeur (DEX)', 'Un estompeur peut rendre son corps intangible.', '', '0', ''),
('exorcisme', 'Exorcisme', '', '', '2', ''),
('expanseur', 'Expanseur (CON)', 'Un expanseur est capable de grandir au point de devenir un véritable géant.', '', '0', ''),
('exploits-physiques', 'Exploits physiques', 'Équipement : tenue de sport, des haltères ou l’équipement approprié à la pratique sportive choisie.', '', '3', ''),
('foudroyeur', 'Foudroyeur (DEX)', 'Un foudroyeur crée et contrôle des décharges électriques.', '', '0', ''),
('furtivite', 'Furtivité', 'Équipement : chapeau ou casquette, paire de jumelle.', '', '1', ''),
('genie', 'Génie (INT)', '', '', '0', ''),
('givreur', 'Givreur (CON)', 'Un geleur, ou givreur, crée du froid et de la glace.', '', '0', ''),
('gobelin', 'Gobelin (CON)', 'Un être sombre et violent autrefois appelé aussi orque ou hobgobelin.', '', '0', ''),
('golem', 'Golem', '', '', '0', ''),
('griffes-de-la-nuit', 'Griffes de la nuit', '', '', '3', ''),
('humilite', 'Humilité', '', '', '2', ''),
('hurleur', 'Hurleur (PER)', 'Un hurleur maîtrise les sons.', '', '0', ''),
('illusions', 'Illusions', '', '', '3', ''),
('incendieur', 'Incendieur (CON)', 'Un incendieur crée et contrôle du feu.', '', '0', ''),
('inquisition-mentale', 'Inquisition mentale', '', '', '2', ''),
('investigation', 'Investigation', 'Équipement : une lampe-torche, une loupe.', '', '2', ''),
('langues', 'Langues', 'Équipement : des romans en langue étrangère.', '', '3', ''),
('leviteur', 'Léviteur (FOR)', 'Un léviteur est capable de voler.', '', '0', ''),
('lumiere-celeste', 'Lumière céleste', '', '', '1', ''),
('lune-dargent', 'Lune d\'argent (CON)', 'Pleine lune', '', '1', ''),
('lune-pourpre', 'Lune pourpre', 'Sang et combat', '', '1', ''),
('lutin', 'Lutin (DEX)', 'Un être frêle et agile, souvent facétieux, aussi appelé farfadet ou kobold.', '', '0', ''),
('magie-elementaire', 'Magie élémentaire', '', '', '1', ''),
('magie-universelle', 'Magie universelle', '', '', '1', ''),
('magnetiseur', 'Magnétiseur (INT)', 'Un magnétiseur contrôle le métal à distance.', '', '0', ''),
('maitre-de-meute', 'Maître de meute', 'Clan et commandement', '', '0', ''),
('malignite-doutre-tom', 'Malignité d\'outre-tombe (DEX)', '', '', '1', ''),
('manipulateur', 'Manipulateur (PER)', 'Un manipulateur déplace des objets par télékinésie.', '', '0', ''),
('mecanique', 'Mécanique', 'Équipement : caisse à outil, une voiture ou une moto (de base ou standard).', '', '1', ''),
('medecine', 'Médecine', 'Équipement : une trousse de secours, un gros livre sur les médicaments, costume', '', '1', ''),
('mere-terre', 'Mère terre', 'Protection et soins', '', '1', ''),
('metal', 'Métal', '', '', '0', ''),
('meute', 'Meute', 'Les créatures qui comptent sur le nombre plutôt que la puissance individuelle, appartiennent à cette catégorie. Ce sont généralement des créatures de petite taille (exemples : les loups ou les hyènes, les gobelins et les kobolds) mais on trouve aussi quelques prédateurs plus gros (lionne, vélociraptor).\r\nLes créatures dotées de cette voie obtiennent la CON en caractéristique supérieure (indiqué par un *). Dans le cas où une créature est rencontrée seule, ignorez ces capacités et retranchez au NC de la créature le rang qu’elle possédait dans cette Voie.', 'creat', '1', ''),
('morpheur', 'Morpheur (CON)', 'Un morpheur peut déformer son corps et changer d’apparence. Quelles que soient les limitations de durée des transformations effectuées, le personnage reprend son apparence normale lorsqu’il dort ou s’il sombre dans l’inconscience.', '', '0', ''),
('musicien', 'Musicien', '', '', '0', ''),
('mystificateur', 'Mystificateur (PER)', 'Le mystificateur est un spécialiste des illusions.', '', '0', ''),
('nageur', 'Nageur (CON)', 'Un nageur maîtrise la force élémentaire de l’eau.', '', '0', ''),
('nuit-noire', 'Nuit noire (DEX)', 'Nouvelle lune', '', '1', ''),
('occulte', 'Occulte', 'Requis : Le personnage doit avoir une valeur d’INT au moins égale à [10 + rang atteint dans la voie]', '', '2', ''),
('ogre', 'Ogre (FOR)', 'Un être massif et puissant mais pas très vif, aussi appelé géant, troll ou encore minotaure.', '', '2', ''),
('peur-des-tenebres', 'Peur des ténèbres', '', '', '1', ''),
('phaseur', 'Phaseur (INT)', 'Un phaseur joue avec le temps et les dimensions, on appelle aussi ce mutant un joueur quantique ou encore un clignoteur.', '', '0', ''),
('pilotage', 'Pilotage', 'Équipement : un véhicule au choix (en accord avec le MJ), tenue de pilote. Si son véhicule est détruit, le personnage pourra faire l’acquisition d’un nouveau véhicule au niveau suivant.', '', '0', ''),
('pnj-recurrent', 'PNJ récurrent', 'Cette voie est créée sur mesure pour un PNJ important et récurrent de votre campagne. Elle doit lui permettre de survivre à l’agressivité bien naturelle de vos PJ à son encontre et lui donner l’occasion de se tirer des plus mauvais pas pour mieux revenir plus tard… et le rendre encore plus détestable !\r\nLes créatures dotées de cette voie obtiennent l’INT et le CHA en caractéristiques supérieures (indiqué par un *).', 'creat', '0', ''),
('predateur', 'Prédateur', 'Les créatures dotées de cette voie obtiennent la DEX et la PER en caractéristique supérieure (indiqué par un *).', 'creat', '0', ''),
('psychologie', 'Psychologie', 'Équipement : des livres de sciences humaines.', '', '1', ''),
('rage-primordiale', 'Rage primordiale (FOR)', '', '', '1', ''),
('reducteur', 'Réducteur (DEX)', 'Un réducteur peut réduire sa taille.', '', '0', ''),
('resilience-sepulcral', 'Résilience sépulcrale (CON)', '', '', '1', ''),
('sauteur', 'Sauteur (DEX)', 'Un sauteur a le pouvoir de se téléporter.', '', '0', ''),
('savoir-impie', 'Savoir impie', '', '', '0', ''),
('sciences', 'Sciences', 'Équipement : livres de science, costume.', '', '3', ''),
('sculpteur', 'Sculpteur (FOR)', 'Un sculpteur contrôle la terre et la roche.', '', '0', ''),
('soins', 'Soins', 'Soins légers et Soins modérés peuvent être utilisés un nombre de fois par jour égal au rang que le personnage a atteint dans cette voie. Au rang 2 par exemple, il peut utiliser chaque capacité 2 fois par jour.', '', '3', ''),
('sombre-magie', 'Sombre magie', '', '', '1', ''),
('sombre-savoir', 'Sombre savoir (INT)', '', '', '0', ''),
('sondeur', 'Sondeur (PER)', 'Un sondeur peut lire les pensées, améliorer ses attaques mentales et détecter les mutants.', '', '0', ''),
('souffleur', 'Souffleur (INT)', 'Un souffleur maîtrise le vent et la tempête', '', '0', ''),
('superpouvoir', 'Superpouvoir', '', 'prest', '0', ''),
('suppresseur', 'Suppresseur (CON)', 'Un suppresseur, aussi appelé nullifieur, empêche les pouvoirs mutants ou magiques de fonctionner. Le personnage ne peut jamais annuler un pouvoir d’un rang supérieur à celui atteint dans la Voie du suppresseur.', '', '0', ''),
('survie', 'Survie', 'Équipement : tenue de randonnée, matériel de camping.', '', '1', ''),
('tentation', 'Tentation', '', '', '1', ''),
('truqueur', 'Truqueur (CHA)', 'Un truqueur affecte les probabilités, il bénéficie sans cesse d’une chance absolument incroyable.', '', '0', ''),
('vagabond-de-la-nuit', 'Vagabond de la nuit (CHA)', 'Lune gibbeuse', '', '0', ''),
('vitaliseur', 'Vitaliseur (CON)', 'Un vitaliseur régénère les tissus vivants. La cible doit être touchée.', '', '0', ''),
('voltigeur', 'Voltigeur (DEX)', 'Un voltigeur est un acrobate surdoué qui défie les lois de gravité.', '', '0', ''),
('voyages', 'Voyages', 'Équipement : des cartes diverses, des devises de plusieurs pays et des photos de souvenirs de pays lointains.', '', '3', '');
