INSERT INTO `cof_familles` (`famille`, `description`) VALUES
('arcanistes          ', 'Arcanistes'),
('combattants         ', 'Combattants'),
('compagnon-arcanistes', 'Compagnon | Arcanistes'),
('compagnon-combattant', 'Compagnon | Combattants'),
('compagnon-hybrides', 'Compagnon | Hybrides'),
('hybride', 'Hybrides'),
('mystiques', 'Mystiques'),
('voyageurs           ', 'Voyageurs');
INSERT INTO `cof_profils` (`profil`, `nom`, `description`, `famille`, `type`) VALUES
('adepte-martial', 'Adepte martial', NULL, 'compagnon-hybrides', '1'),
('adepte-zen', 'Adepte zen', NULL, 'compagnon-hybrides', '1'),
('alchimiste', 'Alchimiste', NULL, 'compagnon-hybrides', '1'),
('archer', 'Archer', NULL, 'hybride', '1'),
('arquebusier         ', 'Arquebusier', 'L’arquebusier est un dur à cuire, un soldat ou un mercenaire habitué des champs de bataille. C’est un spécialiste du combat à distance et plus particulièrement des armes à feu et des explosifs.', 'combattants         ', '0'),
('astrologue', 'Astrologue', NULL, 'compagnon-hybrides', '1'),
('bandit', 'Bandit', NULL, 'hybride', '1'),
('barbare             ', 'Barbare', 'Le barbare est un combattant sauvage issu d’une culture primitive. Il affronte ses ennemis avec rage sans se cacher derrière une lourde armure.', 'combattants         ', '0'),
('barde               ', 'Barde', 'Le barde est un personnage polyvalent qui utilise la magie et le spectacle pour divertir et parvenir à ses fins. Il est aussi habitué aux ruses qu’à la diplomatie.', 'voyageurs           ', '0'),
('bohemien', 'Bohémien', NULL, 'compagnon-hybrides', '1'),
('chaman', 'Chaman', NULL, 'hybride', '1'),
('champion', 'Champion', NULL, 'hybride', '1'),
('chasseur-de-primes', 'Chasseur de primes', NULL, 'hybride', '1'),
('chevalier           ', 'Chevalier', 'Le chevalier est un noble guerrier errant, monté sur un puissant destrier et protégé par une armure lourde. Il parcourt la campagne en quête de torts à redresser et d’occasions de mettre son courage à l’épreuve.', 'combattants         ', '0'),
('chevalier-de-la-mort', 'Chevalier de la mort', NULL, 'hybride', '1'),
('corsaire', 'Corsaire', NULL, 'hybride', '1'),
('courtisane', 'Courtisane', NULL, 'hybride', '1'),
('croise', 'Croisé', NULL, 'compagnon-hybrides', '1'),
('dandy', 'Dandy', NULL, 'compagnon-hybrides', '1'),
('demoniste', 'Démoniste', NULL, 'compagnon-hybrides', '1'),
('devin', 'Devin', NULL, 'compagnon-hybrides', '1'),
('druide              ', 'Druide', 'Le druide est un prêtre de la nature qui défend sa pureté originelle et tire ses pouvoirs de la vie, des animaux et des plantes.', 'mystiques', '0'),
('duelliste', 'Duelliste', NULL, 'hybride', '1'),
('enchanteur', 'Enchanteur', NULL, 'compagnon-hybrides', '1'),
('ensorceleur         ', 'Ensorceleur', 'L’ensorceleur tire son pouvoir d’un talent inné pour la magie. Il pratique une magie subtile à base de tromperie et de contrôle, et possède peu de sorts de destruction massive.', 'arcanistes          ', '0'),
('espion', 'Espion', NULL, 'compagnon-hybrides', '1'),
('forgesort           ', 'Forgesort', 'À la fois artisan et enchanteur, artiste et magicien, le forgesort lie les énergies occultes à la matière qu’il façonne pour créer des artefacts magiques ou des breuvages aux propriétés fantastiques.', 'arcanistes          ', '0'),
('gladiateur', 'Gladiateur', NULL, 'compagnon-hybrides', '1'),
('guerisseur', 'Guérisseur', NULL, 'hybride', '1'),
('guerrier            ', 'Guerrier', 'Le guerrier est un combattant émérite qui n’a pas peur du danger et qui affronte ses ennemis l’arme à la main. De tous les profils de combattant, il est le plus spécialisé et le plus complet dans l’art du combat au corps à corps.', 'combattants         ', '0'),
('invocateur          ', 'Invocateur', 'L’invocateur est un lanceur de sort qui s’est dévoué exclusivement à un aspect spécifique de la magie profane : l’étude de la structure du multivers. Cette connaissance occulte lui permet de comprendre les règles qui lient les différents plans d’existence, d’invoquer à son service des créatures venues d’autres plans ou encore de créer des portails de voyage.', 'compagnon-arcanistes', '0'),
('kensai', 'Kensaï', NULL, 'compagnon-hybrides', '1'),
('lutteur', 'Lutteur', NULL, 'compagnon-hybrides', '1'),
('magelame', 'Magelame', NULL, 'compagnon-hybrides', '1'),
('magicien            ', 'Magicien', 'Le magicien n’est pas qu’un rat de bibliothèque. Il fait aussi appel à ses sorts pour se débarrasser de ses ennemis et pour aider ses compagnons.', 'arcanistes          ', '0'),
('maitre-de-guerre', 'Maitre de guerre', NULL, 'compagnon-hybrides', '1'),
('maitre-des-betes', 'Maitre des bêtes', NULL, 'compagnon-hybrides', '1'),
('mentaliste', 'Mentaliste', NULL, 'compagnon-hybrides', '1'),
('mercenaire', 'Mercenaire', NULL, 'compagnon-hybrides', '1'),
('moine               ', 'Moine', 'Le moine combat à mains nues et utilise le contrôle qu’il a sur son esprit et son corps pour transformer ce dernier en arme de chair.', 'voyageurs           ', '0'),
('necromancien        ', 'Nécromancien', 'Le nécromancien est un lanceur de sort qui s’intéresse aux forces obscures et au pouvoir de la mort. Les nécromanciens sont généralement considérés comme maléfiques et sont peu appréciés. Interprétez-le de façon subtile afin d’en faire un personnage plutôt sombre ou désespéré, mais pas forcément \"mauvais\" !', 'arcanistes          ', '0'),
('ninja', 'Ninja', NULL, 'hybride', '1'),
('ombremage', 'Ombremage', NULL, 'compagnon-hybrides', '1'),
('paladin', 'Paladin', NULL, 'hybride', '1'),
('passeur-dame', 'Passeur d\'âme', NULL, 'compagnon-hybrides', '1'),
('pirate', 'Pirate', NULL, 'compagnon-hybrides', '1'),
('pretre              ', 'Prêtre', 'Le prêtre utilise l’énergie transmise par son dieu pour mener à bien ses missions avec la force de la foi mais aussi celle des armes si nécessaire.', 'mystiques', '0'),
('psionique           ', 'Psionique', 'Le psionique est un être d’exception. Il est né doté de pouvoirs psychiques extraordinaires qu’il a travaillés par la suite à la force de son ego et de son intuition. Profil polyvalent, le psionique peut être développé comme un subtil maître de l’esprit et un combattant à distance ou au contraire comme un guerrier porté par des pouvoirs psychiques.', 'compagnon-arcanistes', '0'),
('repurgateur', 'Répurgateur', NULL, 'hybride', '1'),
('rodeur              ', 'Rôdeur', 'Le rôdeur est à l’aise dans les forêts ténébreuses, où il traque les animaux dangereux et les créatures monstrueuses ou, au contraire, se fait l’ami des bêtes et le protecteur des lieux sauvages.', 'voyageurs           ', '0'),
('sabreur-zen', 'Sabreur zen', NULL, 'compagnon-hybrides', '1'),
('sauvage', 'Sauvage', NULL, 'compagnon-hybrides', '1'),
('seigneur            ', 'Seigneur', 'Le seigneur est un guerrier, mais aussi un homme de cour. Surtout, il est un homme d’honneur. Ces caractéristiques le rapprochent du profil du chevalier, mais il se distingue de ce dernier par des techniques de combat axées sur la vitesse et la maîtrise de l’arc. Le seigneur est lui-même le vassal dévoué d’un suzerain, dans un système de type féodal.', 'compagnon-combattant', '0'),
('sorcier', 'Sorcier', NULL, 'compagnon-hybrides', '1'),
('voleur              ', 'Voleur', 'Le voleur crochète les portes, détecte les pièges et préfère piller les cadavres des ennemis que se salir les mains pendant le combat.', 'voyageurs           ', '0');
