INSERT INTO `cga_familles` (`famille`, `description`) VALUES
('bordure', 'Bordure'),
('centre', 'Centre'),
('espace', 'Espace');
INSERT INTO `cocy_capacites` (`capacite`, `nom`, `limitee`, `sort`, `type`, `description`) VALUES
('1eimplant', 'Premier implant', 0, 0, '0', 'Le personnage peut choisir un implant cybernétique ou biotechnologique d’une valeur maximum de 5 000 NY.'),
('2eimplant', 'Second implant', 0, 0, '0', 'Le personnage peut choisir un implant d’une valeur maximum de 5 000 NY.'),
('3eimplant', 'Troisième implant', 0, 0, '0', 'Le personnage peut choisir un implant d’une valeur maximum de 10 000 NY.'),
('4eimplant', 'Quatrième implant', 0, 0, '0', 'Le personnage peut choisir un implant d’une valeur maximum de 10 000 NY.'),
('5eimplant', 'Cinquième implant', 0, 0, '0', 'Le personnage peut choisir un implant d’une valeur maximum de 15 000 NY.'),
('6emesens', '6ème sens', 0, 0, '0', 'Le mage sait toujours légèrement en avance ce qui va arriver, il gagne un bonus de +1 par rang dans la voie en Init. et en DEF, et un bonus de +2 par rang à tous les tests pour éviter d’être Surpris.'),
('adaptationnaturelle', 'Adaptation naturelle', 1, 1, '0', 'Le chaman devient résistant aux éléments pendant [5 + Mod. de CHA] tours, il ne subit que la moitié des DM de feu, froid, foudre, acide et de tous les DM infligés par des élémentaires. Il est immunisé au poison et peut respirer sous l’eau ou dans une atmosphère toxique.'),
('alliematriciel', 'Allié matriciel', 0, 0, '0', 'Le fixer se paye les services d’un hacker de confiance qui met à sa disposition ses talents en informatique, il peut lui confier une tâche par jour. Ce hacker possède\nun score d’attaque mentale égal à la moitié du niveau du fixer. Il lâche l’affaire s’il subit plus de 3 points de DM par niveau du personnage.'),
('analyseastrale', 'Analyse astrale', 1, 0, '0', 'Lorsqu’il est en perception ou projection astrale, en se concentrant un tour, le personnage peut faire un test de PER pour analyser l’aura d’une créature à moins de 20 mètres : \n• Difficulté 10 : L’état de santé général d’une créature si elle est non-humaine, la présence d’implants cybernétiques (sauf alpha) si l’aura vous est familière. \n• Difficulté 15 : La localisation d’implants cybernétiques alpha ou d’implants biotechnologiques (sauf alpha), un diagnostic général de maladie ou toxine affectant le sujet. \n• Difficulté 20 : L’état émotionnel général de la créature, si son niveau est supérieur, inférieur ou égal au vôtre, la présence d’implants biotechnologiques alpha, la véritable apparence d’une créature. \n• Difficulté 25 : Le niveau, les valeurs de caractéristiques et de cyberlose de la créature, la présence d’une thérapie génique. \nUne fois le résultat obtenu, il n’est pas possible de faire une nouvelle analyse astrale sur la même créature avant 24 heures. Si la créature possède elle-même des rangs dans la Voie astrale, elle peut faire un test d’attaque magique opposé avec le personnage pour interdire l’analyse de son aura.'),
('armeslourdes', 'Spécialiste en armes lourdes', 0, 0, '0', 'Le rigger développe un amour immodéré et une haute compétence pour tout ce qui possède un très gros calibre. Il augmente les DM de toutes les armes de ses drones et de ses véhicules d’une valeur égale au rang atteint dans la voie. De plus, le personnage a désormais accès à toutes les armes lourdes décrites dans la section équipement et peut les utiliser de façon compétente.'),
('armesmultiples', 'Armes multiples', 0, 0, '0', 'Le rigger peut actionner deux armes de véhicule sans pénalité au prix d’une action d’attaque. Le personnage obtient un véhicule interfacé de son choix (sauf véhicule militaire) et celui-ci est gratuitement équipé de deux armes de véhicule.'),
('arroser', 'Arroser', 1, 0, '0', 'Les gangers sont des adeptes du tir à tout va, capables de vider un chargeur en un temps record. Lorsqu’il utilise une arme automatique de FOR 14 ou moins, le personnage peut arroser une zone de 10 mètres de large (ou moins), et chaque cible présente subit une attaque avec un bonus de +2 au test d’attaque et +1d6 aux DM. Le personnage vide son chargeur, il devra utiliser une action de mouvement pour recharger avant de pouvoir se servir à nouveau de cette arme.'),
('artsmartiaux', 'Arts Martiaux', 0, 0, '0', 'Lorsqu’il combat à mains nues, l’adepte inflige [1d6 + Mod. de FOR] DM létaux. Ces DM passent à 1d8 au rang 3 et à 1d10 au rang 5. L’adepte gagne un bonus de +1 en DEF par rang atteint dans la voie.'),
('asdanslamanche', 'As dans la manche', 0, 0, '0', 'Une fois par combat, le joueur peut choisir de remplacer le résultat obtenu sur un d20 par la valeur de CHA du personnage, il applique ensuite tous les modificateurs\nhabituels. Le fixer peut utiliser la capacité As dans la manche plus d’une fois par combat en dépensant 1 PC à chaque utilisation supplémentaire.'),
('assassin', 'Assassin', 0, 0, '0', 'Le chromé est un expert du poignard et un assassin professionnel. Lorsqu’il attaque une cible par surprise, il obtient +1d6 DM par rang dans la voie sur sa première attaque à distance ou au contact s’il utilise un poignard (ou des cybergriffes/digilames). Ce bonus aux DM n’est pas multiplié en cas de critique ni par la capacité Sniper.'),
('attaquemagique', 'Attaque magique', 0, 0, '0', 'L’adepte gagne +1 en attaque magique. Désormais, ses attaques au contact affectent normalement les créatures magiques qui résistent aux attaques conventionnelles (Voie des métacréatures, élémentaire invoqué, etc.). De plus, il peut ajouter son Mod. de CHA aux DM qu’il inflige aux créatures magiques.'),
('attaquesanglante', 'Attaque sanglante', 1, 0, '0', 'Le ganger frappe ou tire là où ça fait mal, il utilise seulement un d12 en attaque au contact ou à distance (au lieu du d20), mais obtient un bonus de +2d6 aux DM.'),
('attaquesournoise', 'Attaque sournoise', 1, 0, '0', 'Lorsqu’il attaque de dos ou par surprise, le ganger ajoute +1d6 par aux DM de son\nattaque au contact ou à distance. Ce bonus passe à +2d6 au rang 3 et +3d6 au rang 5.'),
('avanceracouvert', 'Avancer à couvert', 0, 0, '0', 'Le ganger à l’habitude de devoir progresser sous le feu d’un gang adverse pour arriver au contact (généralement après avoir cramé toutes ses munitions en un rien de temps). Lorsqu’il utilise une action de mouvement, il peut parcourir seulement 10 mètres et gagner un bonus de +3 en DEF contre les attaques à distance pendant 1 tour. Ce bonus de DEF peut être cumulé si le personnage utilise plusieurs fois cette capacité dans le même tour (+6 en DEF pour deux actions de mouvement et 20 mètres, etc.).'),
('barrieremana', 'Barrière de mana', 1, 1, '0', 'Le personnage crée une barrière de mana invisible et infranchissable dans le monde réel comme dans le monde astral (un mur de force qui bloque créatures et objets). Aucun sort ne peut passer à travers. La barrière forme un mur rectiligne de INT mètres sur 4 mètres de haut, ou forme un dôme de ½ INT mètres de diamètre pendant [5 + Mod. d’INT] tours. Si la barrière subit plus de 30 points de DM magiques en un tour, elle est annihilée. Il est possible pour un magicien en perception astrale ou en forme astrale, de lancer ce sort sous forme de barrière astrale uniquement, c’est-à-dire qu’elle n’affecte que les esprits sans forme matérielle (sous forme astrale).'),
('beauparleur', 'Beau-parleur', 0, 0, '0', 'Le fixer gagne +5 aux tests de CHA et il ajoute son Mod. d’INT à tous les tests de CHA.'),
('blousonnoir', 'Blouson noir', 0, 0, '0', 'Le ganger porte un blouson en synthécuir blindé aux couleurs du gang, il semble né avec cette une seconde peau qui se fait totalement oublier. Lorsqu’il porte une veste ou un manteau blindé, la valeur de DEF et de RD de l’armure augmentent de +1. De plus, il ignore la pénalité au score d’Init. d’une veste et subit seulement une pénalité de -2 pour un manteau.'),
('bricoleur', 'Bricoleur', 0, 0, '0', 'Le rigger obtient un bonus de +2 par rang à tous les tests d’électronique, de mécanique, de réparation ou d’armement. Il augmente la vitesse de réparation des drones et des véhicules de +1 par rang (1d6+rang+Mod. d’INT par heure).'),
('bullettime', 'Bullet time', 0, 0, '0', 'L’adepte peut atteindre une vitesse d’esquive surnaturelle qui lui permet d’éviter les balles elles-mêmes. Une fois par tour, il peut ignorer une attaque à distance (même par arme à feu) en réussissant un test d’attaque au contact opposé à la DEF atteinte par l’attaquant. S’il s’agit d’une attaque de zone (sort, grenade, etc.), il divise les DM par deux (aucun test nécessaire dans ce cas).'),
('celebrite', 'Célébrité', 0, 0, '0', 'Le fixer obtient une couverture officielle célèbre dans le milieu du showbiz (vedette de simsens, rock star, journaliste tridéo, etc.), dissociée de son pseudo du milieu des\nombres qu’il garde secret. Toutefois, il peut parfois jouer de cette identité pour s’introduire dans certains milieux ou obtenir des privilèges. De plus, il augmente sa valeur de CHA de +2 et peut désormais lancer deux d20 à chaque fois qu’un test de CHA lui est demandé et garder le meilleur résultat.'),
('combattrecorruption', 'Combattre la corruption', 0, 0, '0', 'Protégé par les esprits de la vie et de la nature, le chaman obtient un bonus égal à son Mod. de PER en DEF contre toutes les attaques provenant d’armes technologiques (armes à feu, laser, explosifs, etc.). Il obtient un bonus égal à son Mod. de CHA en attaque et aux DM au contact et à distance lorsqu’il combat des créatures invoquées (un élémentaire appelé par un mage, par exemple), venues d’un autre monde (esprits incarnés) ou corrompues par un esprit (totem insecte, esprit toxique, etc.).'),
('connaissancerue', 'Connaissance de la rue', 0, 0, '0', 'Le ganger obtient un bonus de +1 par rang dans la voie à tous les tests sociaux lorsqu’il est dans un quartier pauvre ou lorsqu’il négocie avec des personnes issues de milieu défavorisé. Il gagne un bonus supplémentaire de +5 lorsqu’il s’agit d’intimider (quel que soit le milieu social de son interlocuteur).'),
('consommablesagogo', 'Consommables à gogo', 0, 0, '0', 'Le fixer divise par deux le coût des points de cash pour lui-même et accorde un point de cash supplémentaire à chaque membre de son groupe au début de chaque aventure.'),
('contacts', 'Contacts', 0, 0, '0', 'Le fixer choisit un contact par rang atteint dans la voie parmi : rue (et gangs), police (et armée), showbiz (stars et journalisme), corpo, occulte, scientifique (et médical), matrice (informatique et hackers), business (et marché noir). Il obtient un bonus de +5 aux tests de renseignement (ou pour se procurer du matériel) s’il possède le contact de la catégorie correspondant à l’information recherchée. S’il prend deux fois le même contact, le bonus passe à +10.'),
('controleavance', 'Controle avancé', 0, 0, '0', 'Le rigger améliore son contrôle et son immersion sensorielle dans les drones. Désormais, il utilise son score d’attaque mentale pour les attaques effectuées par ses drones même sans les contrôler directement et peut ajouter son Mod. d’INT à leur DEF.'),
('controleenvironmt', 'Contrôle de l\'environnement', 0, 0, '0', 'En milieu urbain, le hacker contrôle en temps réel et utilise à son avantage tout le mobilier urbain, les véhicules de passage, les lumières, les ascenseurs, etc. cela lui permet de créer des diversions ou de se procurer des opportunités. Une fois par combat, le joueur trouvera une explication plausible pour activer un élément du décor et obtenir un effet au choix. Jusqu’à un adversaire par rang dans la voie perd son prochain tour de jeu ou le même nombre d’adversaire subit une agression qui leur inflige automatiquement 1d6 DM par rang dans la voie (par exemple, détourner un bus pour qu’il fauche les adversaires).'),
('coriace', 'Coriace', 0, 0, '0', 'Le ganger gagne +1 en DEF, +1 en Init. et +3 PV. Ce bonus passe à +2 en DEF, +2 en Init. et +6 PV au rang 4 de la voie.'),
('corpsacorps', 'Corps-à-corps', 0, 0, '0', 'Le chromé est un expert en combat au contact. S’il le souhaite, il acquiert au choix des griffes rétractables ou des digilames. Il inflige [1d6 + Mod. de FOR] DM avec\nles griffes ou [1d4 + Mod. de DEX] DM avec les digilames. À chaque tour, il peut réaliser deux attaques de contact au prix d’une action limitée avec un poignard ou en combat à mains nues (griffes et digilames inclues). S’il choisit la version cybernétique de cette capacité, il ajoute +1 à son rang de cyberlose.'),
('courseduvent', 'Course du vent', 0, 0, '0', 'L’adepte se déplace à une vitesse surhumaine. Il couvre une distance de 30 mètres par action de mouvement (la distance normale est de 20 mètres) et se relever est pour lui une action gratuite. Il saute deux fois plus loin ou plus haut qu’un humain ordinaire.'),
('customisation', 'Customisation', 0, 0, '0', 'Le rigger peut appliquer une option différente à chaque véhicule et drone qu’il possède, parmi celles proposées dans la liste qui suit. Armement lourd : +1d6 aux DM, autonomie doublée, blindage : DEF +2 et RD +2, renforcement : +10 PV, arme supplémentaire : une arme de véhicule au choix (seulement pour véhicule), moteur gonflé : +2 AGI ou +10 mètres par action de mouvement, booster : +10 AGI pendant un tour par combat ou 40 mètres de déplacement supplémentaire à ce tour.'),
('deaaz', 'De A à Z', 0, 0, '0', 'Le rigger est à présent capable de concevoir et de fabriquer des appareils de A à Z. Il fabrique et obtient un nouvel appareil de son choix (un drone ou un véhicule non militaire) et il peut y appliquer une option de la capacité Customisation de la Voie des véhicules (pour un total de deux options s’il possède aussi la capacité Customisation).'),
('delivrance', 'Délivrance', 1, 1, '0', 'En touchant sa cible, le chaman annule les pénalités infligées par les sorts, les malédictions et les capacités spéciales d’autres personnages ou de créatures (douleur, mutilation, poisons, etc.).'),
('delugedecoups', 'Déluge de coups', 1, 0, '0', 'À son tour, l’adepte peut effectuer deux attaques à mains nues ou avec une arme de contact sur des cibles de son choix. Alternativement, il peut choisir de faire trois attaques en utilisant un d12 en attaque pour chacune d’elle (au lieu d’un d20).'),
('deverouilleracces', 'Déverrouiller les accès', 0, 0, '0', 'Le hacker se connecte à la matrice ou à un réseau local pour accéder au système et déverrouiller une fermeture électronique située à moins de 50 mètres. En réussissant un test d’attaque mentale contre le niveau de sécurité de l’accès, le hacker peut débloquer une serrure électronique ou, au contraire, la verrouiller par une simple action de mouvement.'),
('dronearme', 'Drone armé', 0, 0, '0', 'Ce drone terrestre de petite taille (50 cm à 1 m pour un poids allant de 20 à 60 kg) est voué au combat. Il est pourvu de chenilles ou de 6 pattes, selon le modèle, le rendant à peu près tout terrain, de blindage et d’armement. Il se déplace seulement de 10 mètres par action de mouvement. \nFOR -2 DEX -2 CON +0 \nINT -3 PER +0 CHA -4 \nDEF 14 PV 10 + niveau Init – \nPistolet mitrailleur léger +rang DM 2d6. \nAutonomie 12 h.'),
('dronedesoutien', 'Drone de soutien', 0, 0, '0', 'Le fixer se paye les services d’un rigger de confiance qui met à sa disposition et pilote pour lui un drone espion une fois par jour pour une durée de 1 heure (voir la capacité de rigger de la voie des drones). Ce rigger pilote le drone à distance, mais c’est le joueur qui détermine ses actions. Il possède un score d’attaque mentale égal à la moitié du niveau du fixer. À partir du rang 4, le fixer peut remplacer le drone espion par un drone armé. Lorsque que la capacité de rigger indique un rang, utilisez à la place le Mod. de CHA du fixer.'),
('droneespion', 'Drone espion', 0, 0, '0', 'Le rigger possède un drone volant de très petite taille (10 à 20 cm environ). Il est équipé de senseurs qui permettent au personnage de voir et d’entendre par son intermédiaire. Lorsque le drone reste à proximité du rigger, il lui donne un bonus de +1d6 en Init. et +5 à tous les tests pour éviter d’être Surpris. Lorsqu’il utilise le drone pour observer quelque chose (action limitée), le rigger obtient un bonus de +2 par rang à tous les tests concernant la perception et lui permet d’utiliser son Mod. d’INT au lieu de celui de PER pour ce type de tests. Le drone espion est petit et difficile à repérer, il réalise les tests de discrétion (DEX) avec deux d20 et garde le meilleur résultat. \nFOR -4 DEX +3* CON -4* \nINT -3 PER +2 CHA -4 \nDEF 15 + rang PV 35 Init – \nAutonomie 12 h.'),
('dronemissile', 'Drone missile', 1, 0, '0', 'Une fois par combat, le rigger peut utiliser un petit missile piloté à distance, il transporte une puissante charge explosive. Il s’agit d’une action limitée et il attaque avec un bonus +5 au score d’attaque mentale du rigger, à une portée maximum de 500 mètres. Le drone inflige 4d6 DM en impact direct et 1d6 DM dans un rayon de 3 mètres.'),
('dronevolant', 'Drone volant', 0, 0, '0', 'Le drone volant mesure environ 60 cm de diamètre et se déplace de 40 mètres par action de mouvement. \nFOR -3 DEX +2 CON +0 \nINT -3 PER +0 CHA -4 \nDEF 16 PV 10 + niveau Init – \nPistolet mitrailleur léger +rang DM 2d6. \nAutonomie 4 h.'),
('empathieanimale', 'Empathie animale', 1, 0, '0', 'Le chaman peut décoder les émotions et les intentions des animaux en les observant pendant un tour. En réussissant un test de CHA difficulté 15, il peut aussi leur faire passer ses propres intentions, établissant ainsi une communication rudimentaire. Il peut communiquer par télépathie avec les animaux proches de son animal totem jusqu’à une distance de 20 mètres.'),
('empathieastrale', 'Empathie astrale', 0, 0, '0', 'Le personnage s’éveille au monde des esprits, il ne possède pas encore le don de double vue, mais il ressent les choses. Il obtient un bonus de +5 aux tests de PER pour détecter la présence de créatures invisibles, d’une illusion, ou sentir s’il est observé depuis le monde astral (PER difficulté 20). S’il se concentre (action limitée), il détecte la magie à une portée de 30 mètres.'),
('entrainement', 'Entraînement', 0, 0, '0', 'Le chromé peut désormais lancer deux d20 à chaque fois qu’un test de FOR ou DEX lui est demandé et conserver le meilleur résultat.'),
('etiquette', 'Etiquette', 0, 0, '0', 'Le personnage connaît les codes et les règles officielles ou tacites du milieu dont il est issu, il sait en reconnaître les membres et interagir avec eux. Il obtient un bonus de +5 à tous les tests de connaissances ou de renseignement associés à son background et un bonus de +5 aux tests de CHA auprès des gens de son milieu d’origine.'),
('expertanalyse', 'Expert en analyse', 0, 0, '0', 'Le hacker développe ses propres logiciels experts d’analyse et d’aide en temps réel des situations de combat. Ils lui permettent de prendre les meilleures décisions tactiques et de conseiller ses alliés. Tous les alliés sur la même zone de combat que le hacker bénéficient d’un bonus de +1 en Init., en attaque et en DEF.'),
('expertmanoeuvres', 'Expert en manoeuvres', 0, 0, '0', 'Le rigger obtient automatiquement un bonus de +5 en DEF lorsqu’il utilise une action de mouvement pour louvoyer (Chapitre 1 p. XXX). Désormais, il lui faut seulement une action de mouvement pour tenter une manoeuvre de percussion.'),
('expertmatrice', 'Expert de la matrice', 0, 0, '0', 'Connecté à la matrice, le hacker accède à tout le savoir du monde et ses logiciels d’analyse téléchargent en permanence les réponses les plus pointues. Lorsqu’un test d’INT est demandé au personnage (dans le monde réel ou virtuel), le joueur lance deux d20 et garde le meilleur résultat. Le hacker gagne aussi un bonus égal au rang atteint dans la voie à son Init. matricielle et à sa DEF matricielle. Il ajoute par ailleurs son rang à son seuil de blessure grave pour tous les DM reçus dans la matrice.'),
('flotdemagie', 'Flot de magie', 0, 0, '0', 'Le mage obtient 1d6 points de mana supplémentaires au début de chaque combat. En dépensant 1 PM supplémentaire lorsqu’il lance un sort, il peut lancer ce sort en action d’attaque plutôt qu’en action limitée. En dépensant 2 PM supplémentaires, il peut lancer ce sort en action de mouvement plutôt qu’en action limitée.'),
('forcedame', 'Force d\'âme', 0, 0, '0', 'Une fois par tour, lorsqu’un sort ou un pouvoir magique affecte l’adepte, il peut faire un test opposé d’attaque magique. En cas de succès, il n’est pas affecté. L’adepte est désormais immunisé aux effets de peur.'),
('formationinitiale', 'Formation initiale', 0, 0, '0', 'Le fixer possède plus d’une corde à son arc. Avant de devenir négociateur, il avait\nun autre métier. Le joueur choisit une capacité de rang 1 de son choix dans n\'importe quelle voie d\'un profil de CO autorisé par le MJ'),
('formationsuperieure', 'Formation supérieure', 0, 0, '0', 'Le fixer choisit une capacité de rang 2 issue du même profil que la capacité de rang 1. De plus, le fixer a toujours besoin de moins de fric pour passer au niveau suivant, retranchez 1 niveau à son niveau réel pour déterminer le fric nécessaire (par exemple, il lui suffit de 5 000 NY pour passer au niveau 3 au lieu de 10 000).'),
('freredesang', 'Frère de sang', 0, 0, '0', 'Le ganger peut faire appel à un ami indéfectible issu du même gang que lui, une fois par aventure, pendant un maximum de 2d6 heures consécutives. En termes de règles, ce personnage est le clone exact du PJ (mêmes caractéristiques, même scores d’attaque et de DM) et son contrôle est assuré par le joueur. Si cet ami meurt, il est définitivement perdu mais le personnage récupère ses 2 points de capacité et peut les investir ailleurs.'),
('grisgristalismans', 'Gris-gris et talismans', 0, 0, '0', 'Le chaman possède de nombreuses petites babioles étranges qui sont autant de gris-gris et autres talismans de protection ou de perception du monde. Il obtient un bonus en DEF et en Init. égal à +1 par rang atteint dans cette voie.'),
('groupedemercenaires', 'Groupe de mercenaires', 0, 0, '0', 'Le fixer peut faire intervenir un groupe de 3d6 mercenaires qu’il emploie une fois par\naventure. Le groupe de mercenaire est considéré comme ayant un NC (niveau de créature) égal à [niveau du fixer +2] et il lui faut 1d6 heures pour être prévenu et opérationnel. Si l’opposition rencontrée à un niveau de rencontre inférieur au NC des mercenaires, la zone est nettoyée. Sinon, les mercenaires infligent automatiquement deux attaques réussies à 2d6 DM à chaque tour pendant un nombre de tour égal à leur NC avant de battre en retraite. Après une rencontre, si le personnage a encore besoin des mercenaires (ce doit être sur le même site et ne pas durer plus de 6 heures), on retranche à leur NC le niveau de la rencontre précédente. En payant 500 NY par niveau, le fixer peut faire intervenir son groupe de mercenaire une seconde fois dans une même aventure.'),
('guerison', 'Guérison', 1, 1, '0', 'Le chaman peut toucher une cible, qui récupère alors tous ses PV perdus et se trouve guérie des poisons, maladies et affaiblissements de caractéristiques. Une créature ne peut bénéficier de ce sort plus d’une fois par jour.'),
('herosmatriciel', 'Héros matriciel', 0, 0, '0', 'Le hacker est une légende de la matrice et un adversaire redoutable. Il ne fait plus qu’un avec la matrice, même lorsqu’il marche dans le monde de la viande. Il augmente son score d’INT de +2 et il peut désormais lancer deux d20 à chaque fois qu’un test d’attaque mentale lui est demandé. De plus, il réduit à présent tous les DM infligés par les systèmes de sécurité de la matrice du double de son Mod. d’INT (RD=[2 x Mod. d’INT]).'),
('imitation', 'Imitation', 1, 1, '0', 'Le mage peut prendre l’apparence d’une créature de taille proche de la sienne (+ ou - 50 cm) qu’il voit au moment de l’incantation ou qu’il connaît bien. Durée [5 + Mod. d’INT] minutes. Le sort prend fin prématurément si le mage est blessé, et une fouille révèle la supercherie.'),
('implantprimaire', 'Implant primaire', 0, 0, '0', 'Le personnage peut choisir un implant cybernétique ou biotechnologique d’une valeur maximum de 5 000 NY.'),
('implantsecondaire', 'Implant secondaire', 0, 0, '0', 'Le personnage peut choisir un implant cybernétique ou biotechnologique d’une valeur maximum de 10 000 NY.'),
('implantsfinaux', 'Implants finaux', 0, 0, '0', 'Le personnage peut choisir deux implants dont la valeur totale ne peut dépasser 25 000 NY. Si les deux implants utilisent la même technologie (C ou B), il perd un seul\npoint sur sa valeur de caractéristique (CHA ou INT) pour l’ensemble des deux implants.'),
('implantsmajeurs', 'Implants majeurs', 0, 0, '0', 'Le personnage peut choisir deux implants dont la valeur totale ne peut dépasser 25 000 NY. Si les deux implants utilisent la même technologie (C ou B), il perd un seul\npoint sur sa valeur de caractéristique (CHA ou INT) pour l’ensemble des deux implants.'),
('implantssuperieur', 'Implants supérieurs', 0, 0, '0', 'Le personnage peut choisir deux implants dont la valeur totale ne peut dépasser 20 000 NY. Chaque implant peut utiliser une technologie différente, il perd un point sur sa valeur de caractéristique (CHA ou INT) pour chaque implant.'),
('incarnationtotem', 'Incarnation du totem', 1, 1, '0', 'Le chaman peut faire appel à la toute-puissance de son esprit totémique, ses traits et sa stature se modifient légèrement pour rappeler son animal totem. Il obtient un bonus de +5 en Init., en DEF, en attaque au contact et à tous ses tests physiques (FOR, DEX, CON et PER) pendant [1d6+Mod. de CHA] minutes\n(1 minute = 6 tours).'),
('increvable', 'Increvable', 0, 0, '0', 'Le ganger augmente sa valeur de CON de +2 et, désormais, le joueur lance deux d20 pour les tests de CON et garde le meilleur résultat.'),
('inebranlable', 'Inébranlable', 0, 0, '0', 'Le calme et le sangfroid sont les principales qualités d’un bon hacker. Lorsque les choses tournent mal, cela devient un atout considérable. Tant que le hacker est sous la moitié de son total de PV, il bénéficie d’un bonus de +2 en Initiative et à tous les tests de réussite, aussi bien dans le monde de la viande que dans la matrice. Lorsque ses PV sont inférieurs ou égaux à son niveau, ce bonus passe à +5.'),
('injonction', 'Injonction', 1, 1, '0', 'Le mage donne un ordre simple que la victime doit pouvoir comprendre. Il doit réussir un test d’attaque magique opposé avec la cible à une portée de 20 mètres. En cas d’échec, la cible exécute l’ordre pendant son prochain tour. Un ordre suicidaire désoriente la cible pendant un tour, mais ne l’oblige pas à se suicider.'),
('insaisissable', 'Insaisissable', 0, 0, '0', 'Le chromé gagne un bonus de +1 par rang dans la voie aux tests de DEX visant à se déplacer avec discrétion ou se camoufler, et autant en DEF.'),
('insensibledouleur', 'Insensible à la douleur', 0, 0, '0', 'Une fois par combat, l’adepte peut noter à part les DM subis après une attaque. Il n’en subira les effets que lorsque le combat sera terminé. L’adepte peut encore agir un tour lorsqu’il tombe à 0 PV.'),
('interfacedarme', 'Interface d\'arme', 0, 0, '0', 'Le chromé est connecté au zoom optique du système d’arme. Il obtient un bonus de +1 en attaque à distance avec les armes à feu et ignore tout malus de couverture de la cible.'),
('invisibilite', 'Invisibilité', 1, 1, '0', 'Le mage se rend invisible pendant [1d6 + Mod. d’INT] minutes. Une fois invisible, personne ne peut plus détecter sa présence ou lui porter d’attaque. Si le magicien attaque ou utilise une capacité limitée, il redevient visible.'),
('invocationelement', 'Invocation d\'un élémentaire', 1, 1, '0', 'Le mage invoque un esprit élémentaire d’un autre monde pendant [1d6 + Mod. d’INT] tours. Celui-ci se manifeste sous la forme d’une silhouette vaguement humanoïde de l’élément concerné. L’élémentaire comprend et exécute des ordres simples comme attaquer, défendre, surveiller. Le niveau et les Mod. indiqués dans les caractéristiques de l’élémentaire font référence à ceux\ndu mage.\nInit. = [niveau +10], DEF = 16, PV = [niveau x 3], \nattaque = niveau, DM = [2d6+Mod. d’INT].\nL’élémentaire réduit de 10 points tous les DM subis par des armes ordinaires\n(armes à distance ou au contact). Il est immunisé aux DM qui correspondent à son élément.'),
('invocespritnature', 'Invocation d\'un esprit de la nature', 1, 1, '0', 'Le chaman invoque un esprit de la nature (animal) pendant [1d6 + Mod. de CHA] tours. Celui-ci se manifeste sous la forme d’un animal fantomatique de taille humaine (plus grand à partir du niveau 10). L’esprit comprend et exécute des ordres simples comme attaquer, défendre, surveiller. Le niveau et les Mod. indiqués dans les caractéristiques de l’esprit font référence à ceux du chaman. Init. = [niveau +20], DEF = 16, PV = [niveau x 3], \nattaque = niveau, DM = [1d6+Mod. de CHA]\nL’esprit réduit de 10 points tous les DM subis par des armes ordinaires (armes à\ndistance ou au contact). Il est actif aussi bien dans le monde réel que dans le\nmonde astral et peut voyager de l’un vers l’autre selon les ordres du chaman.'),
('jeteraterre', 'Jeter à terre', 0, 0, '0', 'Lorsque le ganger réussit une attaque au contact avec un résultat au d20 de 16 ou plus contre une créature humanoïde de taille moyenne, en plus des DM normaux, la\nvictime doit emporter un test opposé de FOR ou tomber au sol (-5 en DEF). Le ganger réalise toujours des attaques sournoises contre un adversaire au sol, même s’il a seulement une action d’attaque…'),
('jouercollectif', 'Jouer collectif', 0, 0, '0', 'Le ganger reproduit le schéma du gang avec ses équipiers. Lorsqu’il combat la même cible qu’un autre membre du groupe, il obtient un bonus de +2 en attaque et\naux DM. De plus, à chaque tour, il peut donner un bonus de coopération de +2\nà tous les tests d’attaque d’un équipier au contact au prix d’une action de mouvement (sans faire de test).'),
('karma', 'Karma', 1, 0, '0', 'Le personnage fait appel à la force de son totem pour influencer son destin et modifier la réalité. Il se concentre pendant un tour et, au tour suivant, il obtient un bonus égal au rang atteint dans la voie sur un lancer de dé de son choix (Init, d20, DM, etc.). Ce bonus ne peut pas s’appliquer à un test en rapport avec la technologie, sauf s’il s’agit de saboter son fonctionnement. Alternativement, le chaman peut dépenser 1 point de mana pour obtenir cet effet en action gratuite. Il doit annoncer son intention avant de prendre connaissance du résultat des dés. Il peut le faire après, mais cela lui coûte 2 PM.'),
('levitation', 'Lévitation', 1, 0, '0', 'En se concentrant, l’adepte peut « léviter » (se déplacer verticalement) à une vitesse de 10 m par tour. En cas de chute, il ne subit aucun DM.'),
('logicielscombat', 'Logiciels de combat', 0, 0, '0', 'Le hacker télécharge dans son propre cerveau des logiciels de compétence pour obtenir des aptitudes de combat qu’il n’a pas. Ces programmes doivent être créés sur mesure par et pour lui-même et leur efficacité est proportionnelle à son talent. Il ajoute son Mod. d’INT en attaque au contact et à distance lorsqu’il combat dans le monde réel.'),
('maitredarme', 'Maïtre d\'arme', 0, 0, '0', 'L’adepte choisit une arme de contact de prédilection (mains nues, katana, épée, etc.). Lorsqu’il attaque avec cette arme, il obtient un bonus de +1 en attaque et aux DM, et il inflige des critiques sur 19-20 au dé (18-20 lorsqu’il emploie un katana).'),
('maitriseastrale', 'Maîtrise astrale', 1, 1, '0', 'Désormais, le personnage se déplace plus rapidement dans l’astral, couvrant jusqu’à CHA x 10 mètres par tour lorsqu’il ne fait pas d’autre action (autrement il se déplace de 10 mètres par action de mouvement). Il devient aussi capable de lancer des sorts dans l’astral lorsqu’il passe en perception astrale ou projection astrale, ou même d’attaquer physiquement une créature astrale. Les attaques réalisées sous forme astrale n’affectent que les créatures astrales. \nEn attaque au contact, il inflige [1d4 + Mod. de CHA] et peut appliquer d’éventuels bonus aux DM (de type compétence d’arts martiaux ou de spécialisation). Le combat se déroule au tour par tour, à la même vitesse que le combat normal. Certaines armes magiques peuvent être emportées dans l’astral et infliger des DM supplémentaires.'),
('maitriseinterieure', 'Maïtrise intérieure', 0, 0, '0', 'Le mage augmente sa valeur de CHA et d’INT de +2. Il peut désormais lancer deux d20 à chaque fois qu’un test de CHA ou d’INT lui est demandé et conserver le meilleur résultat.'),
('manablast', 'Mana blast', 1, 1, '0', 'Le mage choisit une cible visible située à moins de 30 mètres. Il inflige automatiquement 1d6 points de dégâts et, s’il réussit un test d’attaque magique contre la DEF de la cible, les DM passent à [2d6 + Mod. d’INT]. À partir du rang 2, il peut échanger l’énergie brute contre un élément de son choix (feu, froid, foudre). À partir du rang 3, le sort peut affecter une zone de 10 mètres de diamètre (pour un coût de 3 PM). Il inflige [2d6+ Mod. d’INT] DM, la moitié si la victime réussit un test de DEX difficulté 15.'),
('manachoc', 'Mana choc', 1, 1, '0', 'Ce sort provoque un choc étourdissant, il permet de mettre une cible hors d’état de nuire sans la tuer. Le mage choisit une cible dans son champ de vision située à moins de 30 mètres. Sur un test d’attaque magique réussi contre le seuil de blessure grave de la cible (généralement sa valeur de CON), il lui inflige [1d6+Mod. d’INT] DM temporaires (donc réduits du Mod. de FOR de la cible). De plus, la victime doit faire un test de CON difficulté [10 + Mod. d’INT] ou subir un malus de -2 en Init. et à tous ses tests à son prochain tour. \nÀ partir du rang 4, le sort peut affecter une zone de 10 mètres de diamètre (pour un coût de 3 PM).'),
('mangetisme', 'Magnétisme', 1, 1, '0', 'Le mage contrôle le magnétisme autour de lui, il gagne un bonus de +2 en DEF et divise par 2 les DM de toutes les armes ou projectiles métalliques pendant [1d6 + Mod. d’INT] tours.'),
('manipulationfichier', 'Manipulation de fichier', 0, 0, '0', 'Le hacker a besoin d’un succès de moins pour réaliser une suppression ou une falsification de données (respectivement 1 et 2 succès au lieu de 2 et 3, voir les règles).'),
('manipulationsysteme', 'Manipulation de système', 0, 0, '0', 'Le hacker a besoin de deux succès de moins pour réaliser un crash système ou installer un programme espion (respectivement 2 et 3 succès au lieu de 4 et 5).'),
('mirage', 'Mirage', 1, 1, '0', 'Le mage crée une illusion visuelle et sonore immobile d’une durée de [5 + Mod. d’INT] minutes (ou tours si l’illusion est animée). Le volume maximum de l’illusion est de 10 mètres de côté par rang dans la voie (portée 500 m). Divisez ces paramètres par 10 si l’illusion est animée. Interagir avec l’illusion la fait disparaître. L’illusion trompe aussi les capteurs et les implants cybernétiques dans tout le spectre du rayonnement électromagnétique (infrarouge, etc.).'),
('momentdeperfection', 'Moment de perfection', 0, 0, '0', 'Une fois par combat, après 1d6 tours de combat (1d6 tours après la première initiative ou n’importe quel tour suivant), l’adepte peut choisir de réussir toutes ses attaques automatiquement (on ne lance pas les dés) et d’esquiver toutes celles qui le prennent pour cible pendant un tour. Tout semble aller au ralenti autour de lui…'),
('negociateurhorspair', 'Négociateur hors-pair', 0, 0, '0', 'Si le fixer négocie le salaire de son groupe (lui et les autres PJ), il obtient systématiquement une augmentation de 10 % pour tous ses alliés et une augmentation de 20 % pour lui-même. Cette augmentation vient en plus du bonus normal pour un haut Mod. de CHA obtenu par chaque personnage. De plus, le fixer peut négocier le meilleur prix pour le matériel, que ce soit à l’achat ou la revente\n(normalement 50 % du prix d’achat), il obtient un bonus de 10 %.'),
('neutralisersecurites', 'Neutraliser les sécurités', 1, 0, '0', 'En réussissant un test d’attaque mentale contre le niveau de sécurité d’un système d’alarme, le hacker peut le neutraliser dans le monde réel. Le système est mis en veille pour une durée de 2d6 minutes et, sur une réussite critique, il est complètement bloqué. En cas d’échec de plus de 10 points au test d’attaque mentale, l’alarme se met en route immédiatement.'),
('passelectronique', 'Passe électronique', 1, 0, '0', 'Le fixer possède souvent le passe qu’il faut au bon moment, soit un passe falsifié, soit une clef décodée pour ce modèle précis, soit même l’original qu’il s’est procuré\non ne sait trop comment. Pour chaque porte verrouillée qu’il rencontre, il peut faire un unique test d’attaque mentale contre le niveau de sécurité. En cas de succès, il possède le passe approprié et peut ouvrir et refermer la porte à volonté sans déclencher d’alarme.'),
('perceptionastrale', 'Perception astrale', 1, 1, '0', 'La vision du monde astral se superpose à la vision du monde ordinaire (double vue) pendant [5 + Mod. de CHA] tours. Le personnage peut voir les créatures invisibles et les esprits dans le monde réel et astral. Il détecte les illusions et les auras de magies, et peut préciser la nature des sorts en se concentrant un tour. Enfin, il obtient un bonus de +5 aux tests de PER pour détecter les créatures cachées par des moyens conventionnels.'),
('perfectionmentale', 'Perfection mentale', 0, 0, '0', 'L’adepte augmente ses valeurs de PER et d’INT de +2 et il peut désormais lancer deux d20 à chaque fois qu’un test de PER ou d’INT lui est demandé et conserver le meilleur résultat.'),
('perfectionphysique', 'Perfection physique', 0, 0, '0', 'L’adepte augmente ses valeurs de DEX et de CON de +2 et il peut désormais lancer deux d20 à chaque fois qu’un test de DEX ou de CON lui est demandé et conserver le meilleur résultat.'),
('petitfute', 'Petit futé', 0, 0, '0', 'Le fixer est un malin, il sait toujours être au bon endroit au bon moment pour agir vite et en sécurité. Il ajoute son Mod. d’INT en Init. et en DEF. S’il ne possède pas un Mod. d’INT positif, il ajoute malgré tout +1, soit en Init., soit en DEF (à choisir au moment de l’acquisition de la capacité).'),
('petitgenie', 'Petit génie', 0, 0, '0', 'Le rigger augmente sa valeur d’INT de +2 et peut désormais lancer deux d20 à chaque fois qu’un test d’INT lui est demandé. Enfin, il ajoute son Mod. d’INT à son score d’Init. standard.'),
('piloteemerite', 'Pilote émérite', 0, 0, '0', 'Lorsqu’il conduit un véhicule, le personnage obtient un bonus de +1 par rang à tous les tests de pilotage (mais pas d’attaque) et en Init. ; ce bonus passe à +2 par rang lorsqu’il conduit un véhicule interfacé. De plus, le personnage obtient un véhicule de FOR +2 ou moins de son choix (sauf véhicule militaire). Ce véhicule est équipé d’un module de contrôle interfacé.'),
('piraterdefenses', 'Pirater les défenses', 0, 0, '0', 'Les drones ou les systèmes de défense automatiques (tourelles, mitrailleuses, etc.) sont commandés par un réseau, et même les drones autonomes possèdent un accès à distance de secours. Le hacker est capable d’attaquer ces systèmes pour en prendre le contrôle ou les brouiller. Il gagne un bonus de +10 en DEF contre les attaques de tous les systèmes automatisés ou commandés à distance (comme les drones). Au prix d’une action limitée, le hacker peut faire un test d’attaque mentale contre le niveau de sécurité pour désactiver ou prendre le contrôle d’un système d’arme (ou sa DEF si vous n’avez pas de niveau de sécurité). Le système agit alors en suivant les ordres du hacker. Au total, il peut prendre simultanément le contrôle d’un système d’arme par point de son Mod. d’INT. Si le drone est piloté par un rigger, il doit remporter un test opposé d’attaque mentale contre le rigger à chaque tour pour imposer sa volonté.'),
('precision', 'Précision', 0, 0, '0', 'Le chromé obtient un bonus de +2 aux DM des armes à feu jusqu’à la portée de base de l’arme à feu. Si le Mod. de PER du personnage est supérieur, le joueur peut choisir de l’appliquer au lieu du bonus standard de +2.'),
('profilbas', 'Profil bas', 0, 0, '0', 'Le hacker n’est pas seulement un pirate de salon. En tant que mercenaire, il doit souvent engager la viande pour accompagner ses équipiers sur le terrain, il apprend vite à se montrer discret et à baisser la tête sous les balles. Il gagne un bonus de +5 à tous les tests de discrétion (DEX) et, au prix d’une action d’attaque, il gagne +5 en DEF jusqu’à son prochain tour.'),
('projectionastrale', 'Projection astrale', 1, 1, '0', 'Le personnage projette son esprit hors de son corps. L’esprit est invisible dans le monde réel, il se déplace en volant à la vitesse de 10 m par tour. Il peut passer au travers des murs, mais pas des êtres vivants ni des barrières magiques, et il reste relié à son corps d’origine par un long fil argenté qui peut facilement être suivi (dans le monde astral). La durée maximum de la projection astrale est de [1d6 + Mod. de CHA] tours (le personnage peut payer davantage de mana pour prolonger la projection). À la fin, il réintègre instantanément son corps. Le personnage ne perçoit le monde que via sa projection mentale, mais ressent si des DM sont infligés à son corps. \nDans le monde astral, la DEX du personnage est égale à sa PER, sa CON est égale à son INT et sa FOR est égale à son CHA. Les scores d’Init. astrale et de DEF astrale sont égaux à [10 + attaque magique]. Les PV sont communs aux mondes matériel et astral. À ce stade de la voie, le personnage n’est que spectateur, éventuellement proie, et il n’est pas encore capable d’attaquer alors qu’il en projection astrale.'),
('protectionesprits', 'Protection des esprits', 1, 1, '0', 'Le chaman et jusqu’à un nombre d’alliés supplémentaires égal à son Mod. de CHA sont protégés par les esprits. Ils gagnent un bonus de +2 en DEF et, lorsqu’ils subissent des DM infligés par des créatures magiques (élémentaires, esprits, etc.), ils divisent les DM subis par deux.'),
('prototypes', 'Prototypes', 0, 0, '0', 'Le fixer a accès aux derniers prototypes de matériel hightech, généralement déjà fiables, mais pas encore sur le marché. Il obtient un bonus de +1 en attaque et aux DM d’une arme de son choix et à la DEF de son armure. Il ne peut changer l’arme\nou l’armure affectée qu’entre chaque aventure. Ce bonus passe à +2 au rang 4.'),
('quatriemevehicule', 'Quatrième véhicule', 0, 0, '0', 'Le rigger obtient un véhicule interfacé supplémentaire de son choix, il peut choisir n’importe quel véhicule de la liste proposée dans l’équipement, même un véhicule militaire. Les véhicules de grade militaire sont cependant à utiliser avec le plus de discrétion possible, c’està- dire en privilégiant les zones sauvages ou de non-droit… Les aérodynes lourds sont une exception, ils existent en tant que véhicules civils et peuvent être utilisés comme tels si leur armement est camouflé.'),
('rafalecontrolee', 'Rafale Controlée', 1, 0, '0', 'Lorsqu’il réalise un arc de feu (L), le chromé peut éviter de tirer sur les cibles alliées. De plus, son arc de feu est étendu à une zone de 10 mètres de large.'),
('realiteaugmentee', 'Réalité augmentée', 0, 0, '0', 'À l’aide des caméras locales, du réseau satellite, des bases de données et de tous les systèmes de géolocalisation sans fil, le hacker visualise parfaitement le champ de bataille et la plupart de ses protagonistes. Il obtient un bonus de +1 en Init. par rang dans la voie, et un bonus de +1 en DEF dans le monde réel.'),
('recuperation', 'Récupération', 1, 0, '0', 'Une fois par combat, le mage peut récupérer un nombre de points de mana égal au rang atteint dans la voie.'),
('riposte', 'Riposte', 0, 0, '0', 'En plus de ses actions normales, une fois par tour, lorsqu’un adversaire rate une attaque de contact contre l’adepte, il obtient une attaque supplémentaire gratuite contre cet adversaire.'),
('samouraimatriciel', 'Samouraï matriciel', 0, 0, '0', 'Le hacker est un champion numérique et il sait en combattre les programmes les plus dangereux. Il réduit tous les DM infligés par les systèmes de sécurité de la matrice de son Mod. d’INT (RD=Mod. d’INT) et, désormais, lorsqu’il dépasse la DEF d’un système de 10 point ou plus, il obtient une réussite critique qui compte pour 2 succès.'),
('sniper', 'Sniper', 0, 0, '0', 'Le chromé relance tous les 1 obtenus aux dés de DM avec une arme à feu. Lorsqu’il utilise une action de tir visé, il double les DM de son attaque (il les triple s’il obtient une réussite critique) et, s’il utilise un fusil de précision, il ignore le malus de\nportée tant que la cible est surprise.'),
('soins', 'Soins', 1, 1, '0', 'Le chaman peut toucher une cible, qui récupère alors [1d8 + Mod. de CHA] PV perdus. Le chaman peut utiliser ce sort sur lui-même.'),
('soinsgroupes', 'Soins groupés', 1, 1, '0', 'Une fois par combat, le chaman peut libérer une décharge d’énergie bienfaitrice, tous ses compagnons en vue et lui-même récupèrent [1d8 + Mod. de CHA] PV perdus.'),
('sommeil', 'Sommeil', 1, 1, '0', 'Le mage fait un test d’attaque magique (portée 20 mètres). Il endort pour [1d6 + Mod. d’INT] minutes un nombre de créature dont la somme des PV ne doit pas dépasser le résultat du test d’attaque magique multiplié par deux. Toutes les créatures affectées doivent être vivantes et d’un niveau inférieur ou égal au sien. Elles doivent toutes être situées dans une zone de 10 mètres de diamètre. Le sort affecte toujours d’abord les créatures avec le plus faible nombre de PV présentes dans la zone. Il est possible de les réveiller en les giflant (action d’attaque).'),
('specialisteblindage', 'Spécialiste en blindage', 0, 0, '0', 'Le rigger développe une compétence importante sur les blindages et les armures lourdes. Il augmente de +2 la DEF et la RD de tous ses drones et de ses véhicules. Le personnage a désormais accès à toutes les armures lourdes décrites dans la section équipement (en italique) et il divise par deux le malus d’Init. imposé par les armures de tout genre.'),
('specialite', 'Spécialité', 0, 0, '0', 'Chaque gang se spécialise dans une forme ou une autre de combat de rue sur laquelle il fonde sa réputation. Le personnage choisit une des spécialités suivantes : armes de contact +1 en attaque et aux DM, armes à feu +1 en attaque et aux DM, motos +5 à tous les tests de conduite et pas de malus de tir en conduisant, pugilat :\n1d6 DM à mains nues (ce peut être des cybergriffes ou des digilames) et +2 en DEF. Au rang 4, il peut choisir une seconde spécialité.'),
('sursesgardes', 'Sur ses gardes', 0, 0, '0', 'Le ganger est à moitié paranoïaque. Dans la rue, le danger peut survenir à tout moment, d’un tireur embusqué comme d’un gamin défoncé. Le joueur lance deux d20 pour tous les tests de PER et garde le meilleur résultat. Il gagne un bonus de +5 en DEF contre toutes les attaques par surprise et divise par deux les DM des attaques sournoises (ou de capacités similaires comme l’Assassinat de la Voie du Commando).'),
('technomancien', 'Technomancien', 1, 0, '0', 'Le hacker est devenu le représentant d’une nouvelle évolution de l’humanité. Son affinité avec les machines et les ondes émises par les appareils électroniques est telle qu’il peut à présent directement influencer leur fonctionnement sans même passer par la matrice de façon consciente. Au prix d’une action limitée, il peut réaliser un test d’attaque mentale (portée de 20 mètres) contre la DEF d’un drone ou d’un véhicule ou faire un test d’attaque mentale opposé contre une créature porteuse de cyberware (Voie du chrome) ou d’une interface neurale (hacker, rigger). En cas de réussite, il lui inflige un des deux effets suivant au choix : soit un malus de -2 (Init., tests de réussite et DEF) pour une durée de 1d6 tours, soit [2d6 + Mod. d’INT] DM. Si un hacker est pris pour cible, il peut réduire les DM subis comme s’il s’agissait de l’attaque d’un système de sécurité matriciel.'),
('tira2mains', 'Tir à deux mains', 0, 0, '0', 'Le chromé peut tirer des deux mains avec des armes automatiques de poing. À chaque fois qu’il peut réaliser une attaque à distance, il peut faire deux tests d’attaque séparés et infliger les DM correspondants (même lors d’une action limitée). S’il ne vise pas la même cible avec les deux armes, il subit un malus de -5 sur chaque attaque.'),
('tirencourse', 'Tir en course', 0, 0, '0', 'Le combat, tel que le conçoit un ganger, est plein de courses-poursuites, d’invectives, de charges et de replis. Au prix d’une action d’attaque, le personnage est capable de réaliser une attaque avec une arme à feu avec une pénalité de -2 en attaque tout en se déplaçant de 20 mètres ou une attaque sans pénalité et un déplacement de 10 mètres.'),
('tirmortel', 'Tir mortel', 0, 0, '0', 'Le chromé peut choisir d’utiliser un d12 en attaque à distance au lieu du d20 habituel (il ajoute normalement son score d’attaque). Si une telle attaque est réussie, il ajoute 2d6 aux DM.'),
('topdumatos', 'Top du matos', 0, 0, '0', 'Le fixer fournit son groupe en matériel de pointe. Pour chaque mission où il est présent, tous les alliés du fixer obtiennent un bonus de +2 en attaque, aux DM et en DEF.'),
('totem', 'Totem', 0, 0, '0', 'Le chaman doit choisir un totem, un esprit animal auquel il lie son âme et qui influe sur son caractère et ses principes moraux. Le chaman obtient un bonus de +1 en attaque magique et +5 à tous les tests qui correspondent à la compétence ou à l’action indiquée en italique.\n• Ours (ou taureau) est colérique et puissant, intimidation.\n• Panthère (ou chat) est libre et agile, acrobaties.\n• Loup (ou chien) est fidèle et infatigable, course.\n• Renard (ou rat) est rusé et discret, discrétion.\n• Corbeau (ou chouette) est savant et observateur, observation.\n• Aigle (ou lion) est fier et majestueux, persuasion.'),
('totemsuperieur', 'Totem supérieur', 0, 0, '0', 'Le chaman augmente de +2 la valeur de la caractéristique qui correspond à son totem. Désormais, le joueur lance deux d20 à chaque fois qu’un test de cette caractéristique lui est demandé et il garde le meilleur résultat. \nOurs, FOR. Panthère, DEX. Loup, CON. Renard, INT. Corbeau, PER. Aigle, CHA.'),
('toucheatout', 'Touche à tout', 0, 0, '0', 'Dans un gang, chacun apprend un ou deux trucs pour rendre service à la communauté. Le joueur choisit n’importe quelle capacité de rang 1 d’un autre profil cyberpunk.');
INSERT INTO `cocy_capacites` (`capacite`, `nom`, `limitee`, `sort`, `type`, `description`) VALUES
('transedeguerison', 'Transe de guérison', 0, 0, '0', 'Une fois après chaque combat, l’adepte peut méditer pendant 10 minutes et récupérer ainsi [niveau + Mod de CHA] PV.'),
('vifargent', 'Vif argent', 0, 0, '0', 'L’adepte utilise la vitesse et la souplesse plus que la force et la puissance lorsqu’il combat au contact. Lorsqu’il combat à mains nues ou avec une arme légère (poignard, épée, katana), il peut utiliser son Mod. de DEX en attaque au contact plutôt que celui de FOR. De plus, il gagne un bonus de +2 en Init. par rang atteint dans la voie.'),
('vitalitenature', 'Vitalité de la nature', 0, 0, '0', 'Le chaman reçoit un bonus de +5 à tous les tests de survie en pleine nature. Il gagne un nombre de PV supplémentaires égal à son Mod. de CHA. et un bonus en Init. égal à son Mod. de PER.'),
('vol', 'Vol', 1, 1, '0', 'Le magicien peut voler pendant [1d6 + Mod. d’INT] minutes. Sa vitesse de déplacement est la même qu’au sol.');
