INSERT INTO `coc_voies` (`voie`, `nom`, `notes`, `type`, `pfx_deladu`) VALUES
('archeologie', 'Archéologie', 'Équipement : livre sur une civilisation perdue, tenue de randonnée, matériel de fouille.', '0', '2'),
('argent', 'Argent', 'Équipement : vêtements et accessoires de luxe (montre, bijoux, etc.). 1 000 $ ou € en liquide.', '0', '2'),
('armes-a-feu', 'Armes à feu', 'Équipement : un pistolet et un fusil au choix (en accord avec le MJ), des munitions.', '0', '3'),
('arts', 'Arts', 'Équipement : de quoi pratiquer son art, tenue à la mode.', '0', '3'),
('corporations', 'Corporations', 'Le joueur doit choisir une corporation : armée, médecine, université, crime organisé, show-biz, police, etc. Équipement : costume, un objet symbolique.', '0', '3'),
('corps-a-corps', 'Corps à corps', 'Équipement : un poignard, éventuellement une autre arme en accord avec le MJ.', '0', '0'),
('danger', 'Danger', 'Équipement : une trousse de secours, une corde d’escalade.', '0', '0'),
('demon', 'Démon', '', '0', '0'),
('discours', 'Discours', 'Équipement : un lot de carte de visite, une trousse de maquillage.', '0', '0'),
('divination', 'Divination', 'La magie de la divination pourrait éventuellement utiliser la PER au lieu de l’INT comme caractéristique de magie (remplacez alors tous les Mod. d’INT par Mod. de PER).', '0', '1'),
('elixirs', 'Elixirs', 'Chaque jour, le personnage peut créer jusqu’à deux élixirs par rang acquis dans la voie. N’importe quel individu peut consommer un élixir et en bénéficier, même s’il n’est pas lui-même magicien. Produire un lot d’élixirs prend une heure, quel qu’en soit le nombre. Ce sont des mélanges instables qui deviennent inopérants après 24 heures. Le joueur doit donc en noter l’heure de fabrication. Passé 24 heures, le personnage peut régénérer les élixirs en seulement 10 minutes, mais ils comptent alors parmi les potions créées pour la journée (et les PM ne sont pas récupérés). L’alchimiste doit dépenser 1 PM par élixir produit. Ces PM sont déduits du nombre de PM max du personnage et ne sont pas récupérés avant 24 heures.', '0', '3'),
('envouteur', 'Envoûteur', 'La magie de la domination pourrait éventuellement utiliser le CHA au lieu de l’INT comme caractéristique de magie (remplacez alors tous les Mod. d’INT par Mod. de CHA sauf exception signalée par °). Note : Si une cible résiste à un sort de cette voie, elle y est immunisée pendant 24 h.', '0', '2'),
('exorcisme', 'Exorcisme', '', '0', '2'),
('exploits-physiques', 'Exploits physiques', 'Équipement : tenue de sport, des haltères ou l’équipement approprié à la pratique sportive choisie.', '0', '3'),
('furtivite', 'Furtivité', 'Équipement : chapeau ou casquette, paire de jumelle.', '0', '1'),
('golem', 'Golem', '', '0', '0'),
('illusions', 'Illusions', '', '0', '3'),
('investigation', 'Investigation', 'Équipement : une lampe-torche, une loupe.', '0', '2'),
('langues', 'Langues', 'Équipement : des romans en langue étrangère.', '0', '3'),
('magie-elementaire', 'Magie élémentaire', '', '0', '1'),
('magie-universelle', 'Magie universelle', '', '0', '1'),
('mecanique', 'Mécanique', 'Équipement : caisse à outil, une voiture ou une moto (de base ou standard).', '0', '1'),
('medecine', 'Médecine', 'Équipement : une trousse de secours, un gros livre sur les médicaments, costume', '0', '1'),
('metal', 'Métal', '', '0', '0'),
('musicien', 'Musicien', '', '0', '0'),
('pilotage', 'Pilotage', 'Équipement : un véhicule au choix (en accord avec le MJ), tenue de pilote. Si son véhicule est détruit, le personnage pourra faire l’acquisition d’un nouveau véhicule au niveau suivant.', '0', '0'),
('psychologie', 'Psychologie', 'Équipement : des livres de sciences humaines.', '0', '1'),
('sciences', 'Sciences', 'Équipement : livres de science, costume.', '0', '3'),
('soins', 'Soins', 'Soins légers et Soins modérés peuvent être utilisés un nombre de fois par jour égal au rang que le personnage a atteint dans cette voie. Au rang 2 par exemple, il peut utiliser chaque capacité 2 fois par jour.', '0', '3'),
('sombre-magie', 'Sombre magie', '', '0', '1'),
('survie', 'Survie', 'Équipement : tenue de randonnée, matériel de camping.', '0', '1'),
('voyages', 'Voyages', 'Équipement : des cartes diverses, des devises de plusieurs pays et des photos de souvenirs de pays lointains.', '0', '3');
